import { OuroCProvider, SubscriptionCard } from '@ouroc/sdk';

const plans = [
  {
    name: "Basic",
    price: 9,
    interval: "monthly",
    features: ["Basic Support", "Core Features"]
  },
  {
    name: "Pro",
    price: 29,
    interval: "monthly",
    features: ["Priority Support", "AI Features", "Advanced Analytics"]
  },
  {
    name: "Enterprise",
    price: 99,
    interval: "monthly",
    features: ["Dedicated Support", "Custom Integrations", "SLA Guarantee"]
  }
];

function PricingPage() {
  return (
    <OuroCProvider network="devnet">
      <div className="pricing-grid">
        {plans.map((plan, index) => (
          <SubscriptionCard
            key={index}
            planName={plan.name}
            price={plan.price}
            interval={plan.interval}
            features={plan.features}
            onSubscribe={(planData) => console.log('Subscribed:', planData)}
          />
        ))}
      </div>
    </OuroCProvider>
  );
}

templates.code:
// Complete SaaS template with OuroC integration
import { OuroCProvider, SubscriptionCard, useSubscription } from '@ouroc/sdk';

function SaaSApp() {
  return (
    <OuroCProvider network="devnet">
      <Header />
      <Hero />
      <PricingSection />
      <Footer />
    </OuroCProvider>
  );
}

function PricingSection() {
  const { createSubscription, loading } = useSubscription();

  const tiers = [
    {
      name: "Starter",
      price: 19,
      interval: "monthly",
      features: ["Up to 10 users", "Basic analytics", "Email support"]
    },
    {
      name: "Professional",
      price: 49,
      interval: "monthly",
      features: ["Up to 50 users", "Advanced analytics", "Priority support", "API access"]
    },
    {
      name: "Enterprise",
      price: 149,
      interval: "monthly",
      features: ["Unlimited users", "Custom analytics", "Dedicated support", "Custom integrations"]
    }
  ];

  return (
    <div className="pricing-section">
      <h2>Choose Your Plan</h2>
      <div className="pricing-grid">
        {tiers.map((tier, index) => (
          <SubscriptionCard
            key={index}
            planName={tier.name}
            price={tier.price}
            interval={tier.interval}
            features={tier.features}
            onSubscribe={async (plan) => {
              try {
                await createSubscription({
                  planName: plan.planName,
                  amount: plan.price * 1000000,
                  intervalSeconds: getIntervalSeconds(plan.interval)
                });
              } catch (error) {
                console.error('Subscription failed:', error);
              }
            }}
          />
        ))}
      </div>
    </div>
  );
}

